CREATE TABLE t_p60955846_expert_appointment_s.appointments (id SERIAL PRIMARY KEY, specialist_id INTEGER REFERENCES t_p60955846_expert_appointment_s.specialists(id), patient_name VARCHAR(200) NOT NULL, patient_phone VARCHAR(50) NOT NULL, patient_comment TEXT, appointment_date DATE NOT NULL, appointment_time TIME NOT NULL, status VARCHAR(30) DEFAULT 'pending', created_at TIMESTAMP DEFAULT NOW());